mapper.select:
SELECT a.id id,a.num num,a.createtime createtime,a.ctotalpay
ctotalpay,a.cstatus cstatus,a.paytype paytype,c.`code` code,c.`name`
name,a.mallcontractcode mallcontractcode FROM `jnd_contract_applypay` a LEFT JOIN jnd_buy c ON
a.buyid=c.id
where a.cstatus!=4

	and DATE_FORMAT(a.`createtime`,'%Y-%m-%d') = #{createtime}


	and c.`code` like concat('%',#{code},'%')


	and c.`name` like concat('%',#{name},'%')


	and a.paytype = #{paytype}


	and a.cstatus = #{cstatus}


	and a.`mallcontractcode` like concat('%',#{mallcontractcode},'%')

ORDER BY createtime desc limit ${start},${pageSize}

SELECT count(*) FROM `jnd_contract_applypay` a LEFT JOIN jnd_buy c ON
a.buyid=c.id
where a.cstatus!=4

	and DATE_FORMAT(a.`createtime`,'%Y-%m-%d') = #{createtime}


	and c.`code` like concat('%',#{code},'%')


	and c.`name` like concat('%',#{name},'%')


	and a.paytype = #{paytype}


	and a.cstatus = #{cstatus}


	and a.`mallcontractcode` like concat('%',#{mallcontractcode},'%')

SELECT a.id id,a.num num,c.`code` codec,a.createtime
createtime,a.ctotalpay ctotalpay,a.cstatus cstatus,a.paytype
paytype,c.`code` code,c.`name` name,a.mallcontractcode mallcontractcode FROM `jnd_contract_applypay` a
LEFT JOIN jnd_buy c ON a.buyid=c.id
where 1=1

	and DATE_FORMAT(a.`createtime`,'%Y-%m-%d') = #{createtime}


	and c.`code` like concat('%',#{code},'%')


	and c.`name` like concat('%',#{name},'%')


	and a.paytype = #{paytype}


	and a.cstatus = #{cstatus}


	and a.country = #{country}


	and a.`mallcontractcode` like concat('%',#{mallcontractcode},'%')

ORDER BY createtime desc limit ${start},${pageSize}

SELECT count(*) FROM `jnd_contract_applypay` a LEFT JOIN jnd_buy c ON
a.buyid=c.id
where 1=1

	and DATE_FORMAT(a.`createtime`,'%Y-%m-%d') = #{createtime}


	and c.`code` like concat('%',#{code},'%')


	and c.`name` like concat('%',#{name},'%')


	and a.paytype = #{paytype}


	and a.cstatus = #{cstatus}


	and a.country = #{country}


	and a.`mallcontractcode` like concat('%',#{mallcontractcode},'%')

SELECT COUNT(*) FROM `jnd_contract_applypay` a LEFT JOIN jnd_contract c on
a.id=c.apayid where (a.cstatus in(3,4) and c.country=#{country}) or (
a.paytype=1 and a.cstatus=2 and c.country=#{country}) ;
